import { useParams } from "react-router-dom";
import { getItemById } from "../api/itemApis";
import { useState, useEffect } from "react";

export const Item = () => {
  const { id } = useParams();
  const [item, setItem] = useState(null);

  const fetchItem = async () => {
    try {
      const res = await getItemById(id);
      setItem(res.data);
    } catch (err) {
      console.log(err);
      setItem({ error: "Could not load item!" });
      return;
    }
  };

  useEffect(() => {
    fetchItem();
  }, []);

  if (!item) {
    return <p>Loading item...</p>;
  }

  if (item.error) {
    return <p>{item.error}</p>;
  }

  console.log(item);

  return (
    <div className="item-container">
      <h3>{id}</h3>
      <ul>
        {Object.entries(item.data).forEach(([objKey, objValue]) => {
          console.log(objKey, objValue);
          return <Field objKey={objKey} objValue={objValue} />;
        })}
      </ul>
      <p>
        <strong>Total price: {item.totalPrice}</strong>
      </p>
    </div>
  );
};

const Field = ({ objKey, objValue }) => {
  return (
    <p key={objKey}>
      dsd
      {objKey}: {objValue}
    </p>
  );
};
