import { useParams } from "react-router-dom";
import yaml from "js-yaml";
import { useEffect, useState } from "react";
import { getConfByName } from "../api/confsApis";

export const ConfigurationForm = () => {
  console.log("sd");
  const { name } = useParams();

  const [conf, setConf] = useState({ err: null, data: null });

  const getConf = async () => {
    let res;
    try {
      res = await getConfByName(name);
    } catch (err) {
      setConf({ err: `Error while retrieve conf with name ${name}` });
      return;
    }
    console.log(`Conf received by backend`, res.data);
    setConf({ data: yaml.load(res.data) });
  };

  console.log(conf);

  useEffect(() => {
    getConf();
  }, []);

  if (!conf.err && !conf.data) {
    return <p>Loading form...</p>;
  }

  if (conf.err) {
    return <p>{conf.err}</p>;
  }

  return (
    <form>
      <h3>{name}</h3>
      {conf.data.steps.map((s) => {
        return <FormInputWrapper key={s.name} s={s} />;
      })}
      <p style={{ color: "red", marginTop: "50px" }}>* Required Field</p>
    </form>
  );
};

const FormInputWrapper = ({ s }) => {
  return (
    <ul key={s.name}>
      <label htmlFor={s.name}>
        {s.name} {s.required ? <span style={{ color: "red" }}>*</span> : null}
      </label>
      <FormInput step={s} explicitNoneSelect={s.required} />
    </ul>
  );
};

const FormInput = ({ step, explicitNoneSelect }) => {
  console.log(step);
  switch (step.selectionRule) {
    case "EXACTLY_ONE":
      return (
        <select name={step.name} key={step.name}>
          {explicitNoneSelect ? <option value={null}>None</option> : null}
          {step.values.map((v) => {
            return (
              <option key={v.value} value={v.value}>
                {v.description}
              </option>
            );
          })}
        </select>
      );
    case "BOOLEAN":
      return (
        <input key={step.name} type="checkbox" name={step.name} value={1} />
      );
    case "AT_LEAST_ONE_COMPLEX":
      return step.steps.map((s) => {
        return (
          <FormInputWrapper key={s.name} s={s} explicitNoneSelect={true} />
        );
      });
  }
};
