import { useParams } from "react-router-dom";
import yaml from "js-yaml";
import { useEffect, useState } from "react";
import { getConfByName } from "../api/confsApis";

export const ConfigurationForm = () => {
  const { name } = useParams();

  const [conf, setConf] = useState({ err: null, data: null });

  const getConf = async () => {
    let res;
    try {
      res = await getConfByName(name);
    } catch (err) {
      setConf({ err: `Error while retrieve conf with name ${name}` });
      return;
    }
    console.log(`Conf received by backend`, res.data);
    setConf({ data: yaml.load(res.data) });
  };

  console.log(conf);

  useEffect(() => {
    getConf();
  }, []);

  if (!conf.err && !conf.data) {
    return <p>Loading form...</p>;
  }

  if (conf.err) {
    return <p>{conf.err}</p>;
  }

  return (
    <form>
      {conf.data.steps.map((s) => {
        return (
          <div key={s.name}>
            <label htmlFor={s.name}>{s.name}</label>
            <FormInput step={s} />
            {s.required ? <span style={{ color: "red" }}>*</span> : null}
          </div>
        );
      })}
      <p style={{ color: "red" }}>* Required Field</p>
    </form>
  );
};

const FormInput = ({ step }) => {
  switch (step.selectionRule) {
    case "EXACTLY_ONE":
      return (
        <select name={step.name} key={step.name}>
          {step.values.map((v) => {
            return <option value={v.value}>{v.description}</option>;
          })}
        </select>
      );
    case "BOOLEAN":
      return (
        <input key={step.name} type="checkbox" name={step.name} value={1} />
      );
  }
};
