import { useParams } from "react-router-dom";
import yaml from "js-yaml";
import { useEffect, useState } from "react";
import { getConfByName } from "../api/confsApis";

export const ConfigurationForm = () => {
  const { name } = useParams();

  const [conf, setConf] = useState({ err: null, data: null });

  const getConf = async () => {
    let res;
    try {
      res = await getConfByName(name);
    } catch (err) {
      setConf({ err: `Error while retrieve conf with name ${name}` });
      return;
    }
    console.log(`Conf received by backend`, res.data);
    setConf({ data: yaml.load(res.data) });
  };

  console.log(conf);

  useEffect(() => {
    getConf();
  }, []);

  if (!conf.err && !conf.data) {
    return <p>Loading form...</p>;
  }

  if (conf.err) {
    return <p>{conf.err}</p>;
  }

  return (
    <form>
      {conf.data.steps.map((s) => {
        <label>s.name</label>;
      })}
    </form>
  );
};
