import { useParams } from "react-router-dom";
import yaml from "js-yaml";
import { useEffect, useState } from "react";
import { getConfByName } from "../api/confsApis";

export const ConfigurationForm = () => {
  const { name } = useParams();
  const [conf, setConf] = useState({ err: null, data: null });
  const [input, setInput] = useState({});

  const getConf = async () => {
    let res;
    try {
      res = await getConfByName(name);
    } catch (err) {
      setConf({ err: `Error while retrieve conf with name ${name}` });
      return;
    }
    console.log(`Conf received by backend`, res.data);
    setConf({ data: yaml.load(res.data) });

    console.log(conf);

    let initialInput = {};
    for (const step of conf.steps) {
      if (!step.values) {
        continue;
      }
      const preselectedValue = step.values.some((v) => v.preselected);
      initialInput = {
        ...initialInput,
        [step.name]: { value: preselectedValue },
      };
    }
    setInput(initialInput);
  };

  useEffect(() => {
    getConf();
  }, []);

  if (!conf.err && !conf.data) {
    return <p>Loading form...</p>;
  }

  if (conf.err) {
    return <p>{conf.err}</p>;
  }

  console.log(input);

  return (
    <form>
      <h3>{name}</h3>
      {conf.data.steps.map((s) => {
        return (
          <FormInputWrapper
            key={s.name}
            s={s}
            explicitNoneSelect={!s.required}
            setInput={setInput}
            input={input}
          />
        );
      })}
      <p style={{ color: "red", marginTop: "50px" }}>* Required Field</p>
    </form>
  );
};

const FormInputWrapper = ({ s, explicitNoneSelect, setInput, input }) => {
  return (
    <ul key={s.name}>
      <label htmlFor={s.name}>
        {s.name} {s.required ? <span style={{ color: "red" }}>*</span> : null}
      </label>
      <FormInput
        step={s}
        explicitNoneSelect={explicitNoneSelect}
        setInput={setInput}
        input={input}
      />
    </ul>
  );
};

const FormInput = ({ step, explicitNoneSelect, setInput, input }) => {
  switch (step.selectionRule) {
    case "EXACTLY_ONE":
      return (
        <select
          name={step.name}
          key={step.name}
          onChange={(e) =>
            setInput({ ...input, [step.name]: { value: e.target.value } })
          }
          onLoad={(e) => setInput({ ...input, asd: "sad" })}
        >
          {explicitNoneSelect ? <option value={null}>-- None --</option> : null}
          {step.values.map((v) => {
            return (
              <option key={v.value} value={v.value}>
                {v.description}
              </option>
            );
          })}
        </select>
      );
    case "BOOLEAN":
      return (
        <input key={step.name} type="checkbox" name={step.name} value={1} />
      );
    case "AT_LEAST_ONE_COMPLEX":
      return step.steps.map((s) => {
        return (
          <FormInputWrapper
            key={s.name}
            s={s}
            explicitNoneSelect={true}
            setInput={setInput}
            input={input}
          />
        );
      });

    case "ZERO_OR_MORE":
      return (
        <ul>
          {step.values.map((v) => {
            return (
              <div key={v.value}>
                <label htmlFor={v.description}>{v.description}</label>
                <input value={v.value} type="checkbox" name={v.description} />
              </div>
            );
          })}
        </ul>
      );
  }
};
